import React, { useState } from 'react';
import { useDashboard } from './hooks/useDashboard.js';
import { StatsCard } from './components/dashboard/StatsCard.js';
import { ActionCard } from './components/dashboard/ActionCard.js';
import { LoadingSpinner } from './components/common/LoadingSpinner.js';
import { ErrorMessage } from './components/common/ErrorMessage.js';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const { stats, dailyTip, loading, error, refreshData } = useDashboard();

  const renderDashboard = () => {
    if (loading) {
      return (
        <div className="p-6">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Welcome to DynamicBiz Security Awareness
            </h1>
            <p className="text-gray-600">
              Your comprehensive security training and policy management platform
            </p>
          </div>
          <LoadingSpinner size="lg" text="Loading dashboard data..." />
        </div>
      );
    }

    if (error) {
      return (
        <div className="p-6">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-2">
              Welcome to DynamicBiz Security Awareness
            </h1>
            <p className="text-gray-600">
              Your comprehensive security training and policy management platform
            </p>
          </div>
          <ErrorMessage message={error} onRetry={refreshData} />
        </div>
      );
    }

    return (
      <div className="p-6">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Welcome to DynamicBiz Security Awareness
          </h1>
          <p className="text-gray-600">
            Your comprehensive security training and policy management platform
          </p>
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <StatsCard
            title="Policies"
            value={stats?.total_policies || 0}
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            }
            bgColor="bg-blue-100"
            textColor="text-blue-600"
          />

          <StatsCard
            title="Quizzes"
            value={stats?.total_quizzes || 0}
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
            }
            bgColor="bg-green-100"
            textColor="text-green-600"
          />

          <StatsCard
            title="Games"
            value={stats?.total_games || 0}
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.828 14.828a4 4 0 01-5.656 0M9 10h1m4 0h1m-6 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            }
            bgColor="bg-purple-100"
            textColor="text-purple-600"
          />

          <StatsCard
            title="Training"
            value={stats?.total_training_modules || 0}
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 5.477 5.754 5 7.5 5s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.523 18.246 19 16.5 19c-1.746 0-3.332-.477-4.5-1.253" />
              </svg>
            }
            bgColor="bg-orange-100"
            textColor="text-orange-600"
          />
        </div>

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          <ActionCard
            title="Read Policies"
            description="Review and acknowledge company security policies"
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            }
            bgColor="bg-blue-100"
            textColor="text-blue-600"
            onClick={() => alert('Policies feature coming soon!')}
          />

          <ActionCard
            title="Take Quizzes"
            description="Test your security knowledge with interactive quizzes"
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
            }
            bgColor="bg-green-100"
            textColor="text-green-600"
            onClick={() => alert('Quizzes feature coming soon!')}
          />

          <ActionCard
            title="Play Games"
            description="Learn security through fun mini-games and simulations"
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.828 14.828a4 4 0 01-5.656 0M9 10h1m4 0h1m-6 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            }
            bgColor="bg-purple-100"
            textColor="text-purple-600"
            onClick={() => alert('Games feature coming soon!')}
          />

          <ActionCard
            title="Training Modules"
            description="Complete interactive security training courses"
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 5.477 5.754 5 7.5 5s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.523 18.246 19 16.5 19c-1.746 0-3.332-.477-4.5-1.253" />
              </svg>
            }
            bgColor="bg-orange-100"
            textColor="text-orange-600"
            onClick={() => alert('Training modules feature coming soon!')}
          />

          <ActionCard
            title="Reports"
            description="View your progress and compliance reports"
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
            }
            bgColor="bg-red-100"
            textColor="text-red-600"
            onClick={() => alert('Reports feature coming soon!')}
          />

          <ActionCard
            title="Profile"
            description="Manage your account and preferences"
            icon={
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
              </svg>
            }
            bgColor="bg-indigo-100"
            textColor="text-indigo-600"
            onClick={() => alert('Profile feature coming soon!')}
          />
        </div>

        {/* Daily Security Tip */}
        <div className="bg-gradient-to-r from-blue-500 to-purple-600 p-6 rounded-lg text-white">
          <h3 className="text-xl font-semibold mb-2">ðŸ”’ Daily Security Tip</h3>
          <p className="text-blue-100">
            {dailyTip || "Always use strong, unique passwords for each account. Consider using a password manager to generate and store complex passwords securely."}
          </p>
        </div>

        {/* Refresh Button */}
        <div className="mt-6 text-center">
          <button
            onClick={refreshData}
            className="bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors"
          >
            Refresh Dashboard
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <h1 className="text-2xl font-bold text-gray-900">DynamicBiz Security</h1>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                Login
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {renderDashboard()}
      </main>
    </div>
  );
}

export default App;
